// ------------------------------------------------------------
// 1. Obtener la salida del agente
// ------------------------------------------------------------
const rawOutput = $json.output || $json.toolResponse || $json.text || null;

let toolData = null;

// ------------------------------------------------------------
// 2. Intentar parsear directamente lo que venga
// ------------------------------------------------------------
if (rawOutput && typeof rawOutput === "string") {
  try {
    const parsed = JSON.parse(rawOutput);

    // Es una respuesta v√°lida del YNAB-API si contiene success
    if (parsed && typeof parsed === "object" && parsed.success !== undefined) {
      toolData = parsed;
    }
  } catch (_) {
    // No es JSON
  }
}

// ------------------------------------------------------------
// 3. Si a√∫n no hay toolData ‚Üí error real
// ------------------------------------------------------------
if (!toolData) {
  return [{
    json: {
      output:
        "‚ùå Error leyendo la respuesta del servidor.\n" +
        "El agente no devolvi√≥ JSON v√°lido.\n\n" +
        "Debug (100 chars): " +
        (rawOutput ? JSON.stringify(rawOutput).substring(0, 100) : "sin output")
    }
  }];
}

// ------------------------------------------------------------
// 4. Formatear respuesta final seg√∫n acci√≥n YNAB
// ------------------------------------------------------------
let output = "";

switch (toolData.action) {

  case "create_transaction":
    output = `‚úÖ Transacci√≥n creada exitosamente!\n`;
    if (toolData.transaction_id) output += `ID: ${toolData.transaction_id}\n`;
    if (toolData.amount) output += `Monto: $${(toolData.amount / 1000).toLocaleString('es-CO')}\n`;
    if (toolData.payee_name) output += `Comercio: ${toolData.payee_name}\n`;
    if (toolData.date) output += `Fecha: ${toolData.date}`;
    break;

  case "get_accounts":
    output = `üìä Cuentas encontradas (${toolData.count}):\n\n`;
    toolData.accounts.forEach(acc => {
      const balance = (acc.balance / 1000).toLocaleString('es-CO');
      output += `‚Ä¢ ${acc.name}: $${balance}\n`;
    });
    break;

  case "get_categories":
    output = `üìÅ Categor√≠as (${toolData.count} grupos):\n`;
    toolData.categories.forEach(group => {
      output += `\n**${group.name}**\n`;
      group.categories?.forEach(cat => {
        if (!cat.hidden) output += `  ‚Ä¢ ${cat.name}\n`;
      });
    });
    break;

  case "get_transactions":
    output = `üí≥ √öltimas transacciones (${toolData.count}):\n\n`;
    toolData.transactions.slice(0, 10).forEach(txn => {
      const amount = (txn.amount / 1000).toLocaleString('es-CO');
      output += `‚Ä¢ ${txn.date} - ${txn.payee_name || 'N/A'}: $${amount}\n`;
    });
    break;

  case "get_payees":
    output = `üè™ Comercios (${toolData.count}):\n\n`;
    toolData.payees.slice(0, 20).forEach(p => output += `‚Ä¢ ${p.name}\n`);
    if (toolData.count > 20) {
      output += `\n... y ${toolData.count - 20} m√°s`;
    }
    break;

  case "get_account_balance":
    const bal = (toolData.balance / 1000).toLocaleString('es-CO');
    output = `üí∞ Balance de ${toolData.account.name}:\n$${bal}`;
    break;

  default:
    output = toolData.message || "Operaci√≥n completada.";
}

// ------------------------------------------------------------
// 5. Retornar al usuario
// ------------------------------------------------------------
return [{ json: { output } }];
